    public enum QuickMoveNPC {

        PVP(0x1, 0, false, 9070004, 30, "Move to the Battle Mode zone #cBattle Square#, where you can fight against other users.\n#cLv. 30 or above can participate in Battle Square."),
        MIRROR(0x2, 2, true, 9010022, 10, "Use the #c<Dimensional Mirror># to enter Party Quests and other specialty maps."),
        MONSTER_PARK(0x4, 1, true, 9071003, 20, "Move to the party zone \n#cMonster Park#, where you can fight against strong monsters with your party members.\n#cOnly Lv. 20 or above can participate in the Monster Park."),
        WORLD_TRANSPORT(0x8, 5, true, 9000086, 0, "Move to the closest #c<Continent Transportation Station>#."), //Boats, Airplanes
        ISLAND_TRANSPORT(0x10, 6, true, 9000089, 0, "Use a #c<Taxi># to travel to key locations."), //Taxi, Camel
        MARKET(0x20, 3, true, 9000087, 0, "Move to the #c<Free Market># where you can trade items with other players."),
        CRAFTING(0x40, 4, true, 9000088, 30, "Move to #c<Ardentmill>#, the town of Professions.\n#cMust be Lv. 35+"),
        RANDOLF(0x80, 7, false, 0, 10, ""),
        LUCIA(0x100, 8, false, 0, 10, ""),
        CONOR(0x200, 9, false, 0, 10, ""),
        PART_TIME(0x400, 10, true, 9010041, 30, "Accept Part-Time Job payment."),
        HAIR_STYLE(0x800, 13, true, 9000123, 0, "You can get a stylish haircut from Big Headward."),
        FACE_STYLE(0x1000, 14, true, 9201252, 0, "You can get plastic surgery from Plastic Roy.");
        private final int value, type, id, level;
        private final String desc;
        private final boolean show;

        private QuickMoveNPC(int value, int type, boolean show, int id, int level, String desc) {
            this.value = value;
            this.type = type;
            this.show = show;
            this.id = id;
            this.level = level;
            this.desc = desc;
        }

        public final byte getValue() {
            return (byte) value;
        }

        public final boolean check(int flag) {
            return (flag & value) != 0;
        }

        public int getType() {
            return type;
        }

        public boolean show() {
            return show;
        }

        public int getId() {
            return id;
        }

        public int getLevel() {
            return level;
        }

        public String getDescription() {
            return desc;
        }
    }
